import React, { Component } from "react";
import NavBar from "../components/NavBar"

// function Jumbotron({ children }) {
//     return <div className="jumbotron mt-4">{children}</div>;
//   }

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = { bookName: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        alert("A Book Name was submitted: ", + this.state.bookName);
    }

    render() {
        return (
            <div className="container">
                <NavBar />
                <div className="jumbotron jumbotron-fluid">
                    <div className="container">
                        <h1 className="display-4">(React) Google Books Search</h1>
                        <p className="lead"><strong>Search for and Save Books of interest</strong></p>
                        {/* <hr className="my-2"></hr> */}
                        {/* <p>More info</p> */}
                        {/* <p className="lead">
                            <a className="btn btn-primary btn-lg" href="Jumbo action link" role="button">Jumbo action name</a>
                        </p> */}
                    </div>
                </div>
                <div className="search-div bg-light my-5 p-4">
                    <p className="text-left">Book Search</p>
                    {/* <form onSubmit={this.handleSubmit}>
                        <label>
                        Name:
                        <input type="text" value={this.state.value} onChange={this.handleChange} />
                        </label>
                        <input type="submit" value="Submit" />
                    </form> */}
                <form onSubmit={this.handleSubmit}>
                    <label className="book-input">
                        Name
                        <input className="book-input" type="text" value={this.state.bookName} onChange={this.handleChange}  />
                    </label>
                    <div className="text-right mt-2">
                        <input className="btn btn-primary btn-sm" type="submit" value="Search" />
                    </div>
                    </form>
                </div>
            </div>
        )
    }
}


  export default Home;
